// Function to update the current time display
const updateCurrentTime = () => {
  // Get current time
  const now = new Date();
  const hours = now.getHours().toString().padStart(2, '0');
  const minutes = now.getMinutes().toString().padStart(2, '0');

  // Update the current time in the variable
  const currentTimeObj = { ...variables['d9c804b1-ba30-42c0-ae49-efb1e67a2a5e'] };
  currentTimeObj.time = `${hours}:${minutes}`;

  // Determine current period status
  const totalMinutes = hours * 60 + parseInt(minutes);

  // School day starts at 8:00 (480 minutes)
  if (totalMinutes < 480) {
    currentTimeObj.period = "Before first period";
    currentTimeObj.dayStatus = "before_start";
  } 
  // School day ends at 16:00 (960 minutes)
  else if (totalMinutes > 960) {
    currentTimeObj.period = "After last period";
    currentTimeObj.dayStatus = "after_end";
  } 
  else {
    // During school hours
    currentTimeObj.period = "During school hours";
    currentTimeObj.dayStatus = "in_progress";

    // Check if current time matches any lesson time
    const schedule = variables['f52bce2c-9eb3-4d3d-a94d-fea871f9b1f0'];
    for (const lesson of schedule) {
      const [startHour, startMinute] = lesson.startTime.split(':').map(Number);
      const [endHour, endMinute] = lesson.endTime.split(':').map(Number);

      const lessonStartMinutes = startHour * 60 + startMinute;
      const lessonEndMinutes = endHour * 60 + endMinute;

      if (totalMinutes >= lessonStartMinutes && totalMinutes < lessonEndMinutes) {
        currentTimeObj.period = `${lesson.subject} (${lesson.className})`;
        break;
      }
    }
  }

  variables['d9c804b1-ba30-42c0-ae49-efb1e67a2a5e'] = currentTimeObj;
};

// Update immediately
updateCurrentTime();

// Set up interval to update every minute
const intervalId = setInterval(updateCurrentTime, 60000);

// Clean up interval when page unloads
return () => {
  clearInterval(intervalId);
};

