  return '<span style="color: red;">⚠️ Bitte Startdatum wählen</span>';
}

const dayNames = ['Sonntag', 'Montag', 'Dienstag', 'Mittwoch', 'Donnerstag', 'Freitag', 'Samstag'];

const startDate = new Date(recurrence.startDate);
const endDate = new Date(recurrence.endDate);
const weekDays = Array.isArray(recurrence.weekDays) ? recurrence.weekDays : [];

if (weekDays.length === 0) {
  return '<span style="color: red;">⚠️ Bitte Wochentage auswählen</span>';
}

// Build all dates in range matching weekdays
let datesInRange = [];
for (let d = new Date(startDate); d <= endDate; d.setDate(d.getDate() + 1)) {
  if (weekDays.includes(d.getDay())) {
    datesInRange.push(new Date(d));
  }
}

// Map dates to { dayName, dayNum }
let dayObjects = datesInRange.map(d => ({
  dayName: dayNames[d.getDay()],
  dayNum: d.getDate()
}));

// Group consecutive days
let grouped = [];
let tempGroup = [];

for (let i = 0; i < dayObjects.length; i++) {
  if (tempGroup.length === 0) {
    tempGroup.push(dayObjects[i]);
  } else {
    const prev = tempGroup[tempGroup.length - 1];
    if (dayObjects[i].dayNum === prev.dayNum + 1) {
      tempGroup.push(dayObjects[i]);
    } else {
      grouped.push(tempGroup);
      tempGroup = [dayObjects[i]];
    }
  }
}
if (tempGroup.length) grouped.push(tempGroup);

// Format groups (single day or range)
const formattedGroups = grouped.map(group => {
  if (group.length === 1) {
    return `${group[0].dayName} ${group[0].dayNum}.`;
  } else {
    return `${group[0].dayName} ${group[0].dayNum}. - ${group[group.length - 1].dayName} ${group[group.length - 1].dayNum}.`;
  }
});

// Join with commas and "und"
let repeatPhrase = '';
if (formattedGroups.length === 1) {
  repeatPhrase = `Wiederholt sich am ${formattedGroups[0]}`;
} else if (formattedGroups.length === 2) {
  repeatPhrase = `Wiederholt sich am ${formattedGroups[0]} und ${formattedGroups[1]}`;
} else {
  const last = formattedGroups.pop();
  repeatPhrase = `Wiederholt sich am ${formattedGroups.join(', ')} und ${last}`;
}

return repeatPhrase;

