  messages.push("‚ùå daysArray is not an array or is undefined.");
  return messages;
}

let expectedSemester = null;

schoolDays.forEach(day => {
  const key = String(day.number);
  const entry = daysArray.find(obj => obj.hasOwnProperty(key));

  if (!entry) {
    messages.push(`${day.name}: no entry for day ${key}`);
    return;
  }

  const pickupValue = entry[key];
  const semester = entry.valid_for || entry[`${key}_valid_for`];

  if (!pickupValue || pickupValue.trim() === "") {
    messages.push(`${day.name}: missing pickup value`);
  }

  if (!semester || semester.trim() === "") {
    messages.push(`${day.name}: missing semester`);
  } else {
    if (!expectedSemester) {
      expectedSemester = semester;
    } else if (semester !== expectedSemester) {
      messages.push(`${day.name}: mismatched semester`);
    }
  }
});

return messages.length > 0 ? messages : ["‚úÖ All school days have valid pickup info and semester."];

