  const snapshot = JSON.parse(JSON.stringify(newSchedule)); // deep clone

  // Grab full state
  const historyKey = '5c5431d0-9891-49de-bfd1-67d2dd7a8143';
  const indexKey = '13f0d780-ec45-4333-9080-098d9f930e2b';

  let history = variables[historyKey] || [];
  let index = variables[indexKey] ?? -1;

  // ‚õè If mid-history, trim forward stack (only if necessary)
  if (index < history.length - 1) {
    history = history.slice(0, index + 1);
  }

  // üß† Check if new snapshot is different from the last
  const lastSnapshot = history[history.length - 1];
  const isDifferent = JSON.stringify(snapshot) !== JSON.stringify(lastSnapshot);

  if (isDifferent || history.length === 0) {
    history.push(snapshot);
    variables[historyKey] = history;
    variables[indexKey] = history.length - 1;
  }

  return snapshot;
}

