const assignments = variables['af517f07-181d-4118-a343-8193085cf571'] ?? [];
const allCourses = collections['96d96747-58c8-4dec-a084-0d22a00f645c']?.data ?? [];

const dayId = String(context.item.parent?.data?.day_id).trim();  // Fix: access nested day_id
const yearGroup = Number(context.item.data);

// Step 1: Get course IDs assigned to this day
const dayCourseIds = assignments
  .filter(a => a.day_id === dayId)
  .map(a => a.course_id);

// Step 2: Helper for mixed-format year group fields
const isYearIncluded = (course, yearGroup) => {
  const raw = course.is_for_year_g;

  if (Array.isArray(raw)) {
    return raw.includes(yearGroup);
  }

  if (typeof raw === 'string' && raw.startsWith('{')) {
    const parts = raw.slice(1, -1).split(',').map(x => Number(x.trim()));
    return parts.includes(yearGroup);
  }

  return false;
};

// Step 3: Filter only matching courses
const matchingCourses = allCourses.filter(course =>
  dayCourseIds.includes(course.id) && isYearIncluded(course, yearGroup)
);

// Step 4: Sum available spots
const totalFree = matchingCourses.reduce((sum, course) => {
  const max = course.max_students ?? 0;
  const enrolled = course.student_count ?? 0;
  return sum + Math.max(0, max - enrolled);
}, 0);

// ‚úÖ Return only the number
return totalFree;

