const getYearGroups = (raw) => {
  if (Array.isArray(raw)) return raw;
  if (typeof raw === 'string' && raw.startsWith('{')) {
    return raw.slice(1, -1).split(',').map(x => Number(x.trim()));
  }
  return [];
};

// Step 1: Get course IDs assigned to this day
const dayCourseIds = assignments
  .filter(a => a.day_id === dayId)
  .map(a => a.course_id);

// Step 2: Filter and compute fair share
const totalFairSpots = allCourses.reduce((sum, course) => {
  if (!dayCourseIds.includes(course.id)) return sum;

  const yearGroups = getYearGroups(course.is_for_year_g);
  if (!yearGroups.includes(yearGroup)) return sum;

  const max = course.max_students ?? 0;
  const groupCount = yearGroups.length || 1; // Avoid division by 0

  return sum + (max / groupCount);
}, 0);

// Final rounded result
return Math.round(totalFairSpots);

