const liveSchedule = collections['ee9baa8f-ba17-4f4b-b915-bb972423be13']?.data ?? [];

const courseId = context.item.data?.['course_id'];
const currentDayId = String(context.item.data?.['day_id']);
const currentPeriodId = context.item.data?.['period_id'];
const currentRoom = context.item.data?.['room_id'];
const currentRoomId = currentRoom?.id;
const currentRoomName = currentRoom?.name ?? 'unknown';
const currentTeacherIds = (context.item.data?.['staff_ids'] ?? []).map(s => s.id);
const currentTeacherNames = (context.item.data?.['staff_ids'] ?? []).map(s => s.full_name);

// Find the live schedule entry for this course
const liveEntry = liveSchedule.find(entry => entry.course_id === courseId);

// Safety fallback ‚Äî if somehow the entry doesn't exist, say nothing
if (!liveEntry) return "";

const changes = [];

if (String(liveEntry.day_id) !== currentDayId) {
  changes.push(`üìÖ Day changed (from ${liveEntry.day_name_en ?? 'unknown'})`);
}

if (liveEntry.period_id !== currentPeriodId) {
  changes.push(`‚è∞ Period changed (from ${liveEntry.period_label ?? 'unknown'})`);
}

if (liveEntry.room_id !== currentRoomId) {
  changes.push(`üè´ Room changed (from ${liveEntry.scheduled_room_name ?? 'unknown'} to ${currentRoomName})`);
}

const liveTeacherIds = liveEntry.teacher_ids ?? [];
const liveTeacherNames = liveEntry.teacher_names ?? [];

const sameTeachers =
  currentTeacherIds.length === liveTeacherIds.length &&
  currentTeacherIds.every(id => liveTeacherIds.includes(id));

if (!sameTeachers) {
  const fromNames = liveTeacherNames.join(', ') || 'unknown';
  const toNames = currentTeacherNames.join(', ') || 'unknown';
  changes.push(`üë©‚Äçüè´ Teacher changed (from ${fromNames} to ${toNames})`);
}

return changes.length > 0 ? changes.join('\n') : '‚úÖ No changes';

