const dayId = String(context.item.parent?.data?.['day_id']).trim();
const periodId = String(variables['b99a8b46-70c2-4f04-ac6f-90044620c4a5']).trim();
const courseId = String(context.item.data?.course_id).trim();

// Get current assignment
const current = assignments.find(a =>
  a.day_id === dayId &&
  a.period_id === periodId &&
  a.course_id === courseId
);

// If no current assignment or no room, no conflict possible
