  const history = variables['5c5431d0-9891-49de-bfd1-67d2dd7a8143'];

  if (historyIndex > 0) {
    const newIndex = historyIndex - 1;
    variables['13f0d780-ec45-4333-9080-098d9f930e2b'] = newIndex;
    return JSON.parse(JSON.stringify(history[newIndex]));
  }

  // ‚õî At index 0, return current snapshot and do NOT go further back
  return JSON.parse(JSON.stringify(history[0]));
}

return undoSchedule();

function redoSchedule() {
  const historyIndex = variables['13f0d780-ec45-4333-9080-098d9f930e2b'];
  const history = variables['5c5431d0-9891-49de-bfd1-67d2dd7a8143'];

  // Can only go forward if not already at the latest step
  if (historyIndex < history.length - 1) {
    const newIndex = historyIndex + 1;
    variables['13f0d780-ec45-4333-9080-098d9f930e2b'] = newIndex;
    return JSON.parse(JSON.stringify(history[newIndex]));
  }

  // No forward available ‚Äì return current state
  return JSON.parse(JSON.stringify(history[historyIndex]));
}

return redoSchedule();

