// -- Input Data
const draft = context.item.data?.['current_version'] ?? [];

// -- Utility: Create safe composite key (if needed later)
function generateKey(item) {
  const day_id = item.day_id ?? 'NO_DAY';
  const period_id = item.period_id ?? 'NO_PERIOD';
  const rawId = item.course_id ?? item.class_id ?? 'NO_ID';
  const normalizedId = typeof rawId === 'string' ? rawId.toLowerCase() : rawId;
  return `${day_id}|${period_id}|${normalizedId}`;
}

// -- Normalize draft into a map (optional, in case of deduping by key)
const draftMap = {};
draft.forEach(item => {
  const key = generateKey(item);
  draftMap[key] = {
    ...item,
    isDraft: true,
    currentlyScheduled: true,
    staff_ids: item.staff_ids ?? [],
    teacher_names: item.teacher_names ?? [],
    scheduled_room_name: item.scheduled_room_name ?? '',
    room_name: item.room_name ?? '',
    class_name: item.class_name ?? '',
    course_name: item.course_name ?? '',
    day_name_de: item.day_name_de ?? '',
    day_name_en: item.day_name_en ?? '',
    block_number: item.block_number ?? null,
    display_cell: item.display_cell ?? '',
    subject_name: item.subject_name ?? '',
    subject_color: item.subject_color ?? null,
    subject_icon_id: item.subject_icon_id ?? null,
    class_grade_level: item.class_grade_level ?? null,
  };
});

// -- Final array
const normalizedDraftOnly = Object.values(draftMap);

return normalizedDraftOnly;

