// Get staff collection for mapping
const staffCollection = collections['c7aa9144-7d7f-4269-a7ba-af0ea93ff8a1']?.data ?? [];

// Use only the current schedule selections (new draft = no merge needed)
const raw = collections['ee9baa8f-ba17-4f4b-b915-bb972423be13']?.data ?? [];

// Create a lookup map of staff by ID
const staffMap = {};
staffCollection.forEach(staff => {
  if (staff?.id) {
    staffMap[staff.id] = staff;
  }
});

// Normalize and deduplicate
const seen = new Set();
const normalizedAssignments = [];

raw.forEach(item => {
  const key = `${item.day_id}|${item.period_id}|${item.course_id}`;
  if (!seen.has(key)) {
    seen.add(key);

    // Normalize staff_ids to full staff objects
    let staff_ids = [];

    if (Array.isArray(item.staff_ids)) {
      staff_ids = item.staff_ids.map(staff =>
        typeof staff === 'object' && staff !== null && staff.id
          ? staffMap[staff.id] ?? staff
          : staffMap[staff] ?? { id: staff }
      );
    } else if (Array.isArray(item.teacher_ids)) {
      staff_ids = item.teacher_ids.map(id => staffMap[id] ?? { id });
    }

    normalizedAssignments.push({
      day_id: String(item.day_id ?? '').trim(),
      period_id: String(item.period_id ?? '').trim(),
      course_id: String(item.course_id ?? '').trim(),
      staff_ids,
      room_id: item.room_id ?? null
    });
  }
});

return normalizedAssignments;

