const staffCollection = collections['c7aa9144-7d7f-4269-a7ba-af0ea93ff8a1']?.data ?? [];
const roomCollection = collections['44cd4f9d-216b-4f59-aa49-55fb859e5575']?.data ?? [];
const raw = wwFormulas.merge(
  context.item.data?.['current_version'],
  collections['ee9baa8f-ba17-4f4b-b915-bb972423be13']?.data
) ?? [];

// Create lookup maps
const staffMap = {};
staffCollection.forEach(staff => {
  if (staff?.id) staffMap[staff.id] = staff;
});

const roomMap = {};
roomCollection.forEach(room => {
  if (room?.id) roomMap[room.id] = room;
});

// Normalize and deduplicate
const seen = new Set();
const normalizedAssignments = [];

raw.forEach(item => {
  const key = `${item.day_id}|${item.period_id}|${item.course_id}`;
  if (!seen.has(key)) {
    seen.add(key);

    // Normalize staff_ids to full staff objects
    let staff_ids = [];

    if (Array.isArray(item.staff_ids)) {
      staff_ids = item.staff_ids.map(staff =>
        typeof staff === 'object' && staff !== null && staff.id
          ? staffMap[staff.id] ?? staff
          : staffMap[staff] ?? { id: staff }
      );
    } else if (Array.isArray(item.teacher_ids)) {
      staff_ids = item.teacher_ids.map(id => staffMap[id] ?? { id });
    }

    // Normalize room_id to full room object
    let room_id = null;
    const rawRoom = item.room_id;

    if (Array.isArray(rawRoom)) {
      const first = rawRoom[0];
      room_id = typeof first === 'object' && first !== null
        ? first
        : roomMap[first] ?? null;
    } else if (typeof rawRoom === 'object' && rawRoom !== null) {
      room_id = rawRoom;
    } else {
      room_id = roomMap[rawRoom] ?? null;
    }

    normalizedAssignments.push({
      day_id: String(item.day_id).trim(),
      period_id: String(item.period_id).trim(),
      course_id: String(item.course_id).trim(),
      staff_ids,
      room_id
    });
  }
});

return normalizedAssignments;

