}

if (!recurrence.startDate || isNaN(new Date(recurrence.startDate).getTime())) {
    return '<span style="color: red;">‚ö†Ô∏è Bitte Startdatum w√§hlen</span>';
}

const unitMap = {
    day: 'Tag',
    week: 'Woche',
    month: 'Monat',
    year: 'Jahr'
};

const dayNames = ['Sonntag', 'Montag', 'Dienstag', 'Mittwoch', 'Donnerstag', 'Freitag', 'Samstag'];

const num = recurrence.repeatEveryNumber || 1;
const unit = unitMap[recurrence.repeatEveryUnit] || 'Tag';

let repeatPhrase = '';
if (num === 1) {
    repeatPhrase = `Wiederholt sich jede ${unit}`;
} else if (num === 2) {
    repeatPhrase = `Wiederholt sich jede zweite ${unit}`;
} else {
    repeatPhrase = `Wiederholt sich alle ${num} ${unit}${unit === 'Tag' ? 'e' : 'n'}`;
}

let dayPhrase = '';
if (unit === 'Woche' && Array.isArray(recurrence.weekDays) && recurrence.weekDays.length > 0) {
    const days = recurrence.weekDays.map(i => dayNames[i]);
    if (days.length === 1) {
        dayPhrase = ` am ${days[0]}`;
    } else if (days.length === 2) {
        dayPhrase = ` am ${days[0]} und ${days[1]}`;
    } else {
        const last = days.pop();
        dayPhrase = ` am ${days.join(', ')} und ${last}`;
    }
}

let endPhrase = '';
if (recurrence.endDate) {
    const endDate = new Date(recurrence.endDate);
    const formattedEnd = endDate.toLocaleDateString('de-DE', {
        day: '2-digit',
        month: 'short',
        year: 'numeric'
    });
    endPhrase = ` bis zum ${formattedEnd}`;
}

return `${repeatPhrase}${dayPhrase}${endPhrase}`;

