// Return early if no year groups
  if (!yearGroups || !Array.isArray(yearGroups) || yearGroups.length === 0) {
    return "No years";
  }

  // Sort the year groups numerically
  const sortedYears = [...yearGroups].sort((a, b) => a - b);

  // Find consecutive ranges
  const ranges = [];
  let rangeStart = sortedYears[0];
  let rangeEnd = sortedYears[0];

  for (let i = 1; i < sortedYears.length; i++) {
    if (sortedYears[i] === rangeEnd + 1) {
      // Continue the current range
      rangeEnd = sortedYears[i];
    } else {
      // End the current range and start a new one
      if (rangeStart === rangeEnd) {
        ranges.push(`${rangeStart}`);
      } else {
        ranges.push(`${rangeStart}-${rangeEnd}`);
      }
      rangeStart = sortedYears[i];
      rangeEnd = sortedYears[i];
    }
  }

  // Add the last range
  if (rangeStart === rangeEnd) {
    ranges.push(`${rangeStart}`);
  } else {
    ranges.push(`${rangeStart}-${rangeEnd}`);
  }

  // Join the ranges with commas
  return `Year${ranges.length > 1 ? 's' : ''}: ${ranges.join(', ')}`;

