  if (!studentsCollection || !studentsCollection.data || !Array.isArray(studentsCollection.data)) {
    return [];
  }

  // Filter students based on search query and class filter
  let filteredStudents = studentsCollection.data;

  // Apply class filter if search is empty and class filter is set
  if (!searchQuery.trim() && classFilter) {
    filteredStudents = filteredStudents.filter(student => student.class_id === classFilter);
  }

  // Apply search filter if search query exists (overrides class filter)
  if (searchQuery.trim()) {
    const lowercaseQuery = searchQuery.toLowerCase();
    filteredStudents = studentsCollection.data
      .filter(student => {
