  const endDateValid = !!formData.endDate;
  const timeTypesValid = Array.isArray(formData.timeTypes) && formData.timeTypes.length > 0;
  const durationValid = !!formData.duration && !isNaN(formData.duration) && formData.duration > 0;

  // Check date range validity
  let dateRangeValid = true;
  if (startDateValid && endDateValid) {
    const startDate = new Date(formData.startDate);
    const endDate = new Date(formData.endDate);
    dateRangeValid = startDate < endDate;
  }

  // Check schedule validity based on selected time types
  let scheduleValid = true;
  if (timeTypesValid) {
    if (formData.timeTypes.includes('custom')) {
      // At least one custom time should be set
      const customTimes = formData.schedule?.custom || {};
      scheduleValid = Object.keys(customTimes).length > 0 && Object.values(customTimes).some(time => !!time);
    }

    if (formData.timeTypes.includes('flexband')) {
      // At least one flexband day should be selected
      const flexbandDays = formData.schedule?.flexband || [];
      scheduleValid = scheduleValid && flexbandDays.length > 0;
    }
  }

  // Update errors object
  const errors = {
    courseName: !courseNameValid,
    maxKids: !maxKidsValid,
    room: !roomValid,
    gradeLevels: !gradeLevelsValid,
    startDate: !startDateValid,
    endDate: !endDateValid,
    dateRange: !dateRangeValid && startDateValid && endDateValid,
    timeTypes: !timeTypesValid,
    duration: !durationValid,
    schedule: !scheduleValid
  };

  // Update errors in component variables
  context.component.variables['errors'] = errors;

  // Return overall validation result
  return courseNameValid && maxKidsValid && roomValid && gradeLevelsValid && 
         startDateValid && endDateValid && dateRangeValid && 
         timeTypesValid && durationValid && scheduleValid;

