generate_lesson_dates.code:
const startDate = new Date(variables['c9d93431-7e0e-4bc6-968c-0b95861c432d-value']);
const endDate = new Date(variables['e04665a8-5be8-4dce-9939-9484e0c55f5b-value']);
const dayOfWeek = variables['27609e30-dced-4b3f-aa0e-e90a1b53bf62-value'];

// Convert day name to number (0 = Sunday, 1 = Monday, etc.)
const dayMap = {
    'Sunday': 0, 'Monday': 1, 'Tuesday': 2, 'Wednesday': 3,
    'Thursday': 4, 'Friday': 5, 'Saturday': 6
};
const targetDay = dayMap[dayOfWeek];

// Find first occurrence of target day
let currentDate = new Date(startDate);
while (currentDate.getDay() !== targetDay) {
    currentDate.setDate(currentDate.getDate() + 1);
}

// Generate all lesson dates
const lessonDates = [];
while (currentDate <= endDate) {
    lessonDates.push(new Date(currentDate));
    currentDate.setDate(currentDate.getDate() + 7);
}

// Create single lesson record for each date with teacher_ids array
const lessons = lessonDates.map(date => ({
    course_id: variables['5a272dd8-dd3b-409f-8fa5-e2ab1ffe1a66']?.['id'],
    lesson_date: date.toISOString().split('T')[0],
    teacher_ids: variables['56afbe20-4e29-4fd0-8ec6-dd7904055e26-value'],
    is_cancelled: false,
    notes: null,
    school_id: variables['4550dc66-2d3f-4b40-a921-c01a2725221e']?.['id'],
    start_time: context.workflow['7fa7100f-7db1-423e-aa7b-fb0d70218d44'].result.start_time,
    end_time: context.workflow['7fa7100f-7db1-423e-aa7b-fb0d70218d44'].result.end_time,
    is_generated: true,
    course_schedule_id: context.workflow['7fa7100f-7db1-423e-aa7b-fb0d70218d44'].result.id
}));

return lessons;

