// Get search term and student data
const searchTerm = variables['b728b7a1-e8df-41f8-a9b2-f1054e3f57df-value']?.toLowerCase() || '';
const students = collections['881b151d-4f64-4d96-a74b-812bb9282cb8']?.data || [];
const showOnlyUnmarked = variables['99ad1e0e-ed33-4fcc-9b31-63ff7e175ba7-value'];

// If no search term, return only checked-in students (unless showOnlyUnmarked is true)
if (!searchTerm) {
    if (showOnlyUnmarked) {
        return students.filter(student => !student.presence_status || student.presence_status !== 'present');
    }
    return students.filter(student => student.presence_status === 'present');
}

// Fuzzy search function
function fuzzyMatch(str, pattern) {
    str = str?.toLowerCase() || '';
    pattern = pattern.toLowerCase();
    let patternIdx = 0;
    let strIdx = 0;
    while (strIdx < str.length && patternIdx < pattern.length) {
        if (str[strIdx] === pattern[patternIdx]) {
            patternIdx++;
        }
        strIdx++;
    }
    return patternIdx === pattern.length;
}

// Filter students based on fuzzy search across multiple fields
// When searching, show all matching students regardless of check-in status
return students.filter(student => {
    const matchesSearch = fuzzyMatch(student.first_name, searchTerm) ||
        fuzzyMatch(student.last_name, searchTerm) ||
        fuzzyMatch(student.class_name, searchTerm);

    // If showOnlyUnmarked is true, only show unchecked students
    if (showOnlyUnmarked) {
        return matchesSearch && (!student.presence_status || student.presence_status !== 'present');
    }

    return matchesSearch;
});

