const currentStudent = variables['a5465bfa-e22b-46bb-b13e-a7d1da7d93da'] || {}
const currentId = currentStudent?.profile_id
const currentLastName = currentStudent?.last_name?.toLowerCase() || ''
const siblingIds = (currentStudent?.siblings || []).map(s => s.profile_id)

const search = variables['2214c72f-77cb-4d1d-8bb1-20d88b43c4e7-value']?.toLowerCase() || ''

return students.filter(student => {
  const id = student?.profile_id
  const isNotCurrent = id !== currentId
  const isNotSibling = !siblingIds.includes(id)

  if (!isNotCurrent || !isNotSibling) return false

  if (!search) {
    // No search input â€” match same last name only
    return student?.last_name?.toLowerCase() === currentLastName
  }

  // Search input present â€” fuzzy search across multiple fields
  const fieldsToSearch = [
    student?.first_name,
    student?.last_name,
    student?.nickname,
    student?.class_name
  ].filter(Boolean)

  return fieldsToSearch.some(field =>
    field.toLowerCase().includes(search)
  )
})

