// Collections
const daysCollection = collections['9750c008-5b3f-4bd0-8402-d792ff4b5b3c']?.data ?? [];
const periodsCollection = collections['cc215fad-c61a-4270-9a1b-b50c3b1bfcc9']?.data ?? [];
const staffCollection = collections['c7aa9144-7f7f-4269-a7ba-af0ea93ff8a1']?.data ?? [];
const classCollection = collections['b8d0c0e2-1e1a-49c1-b2e7-6d468ab46eb2']?.data ?? [];
const subjectCollection = collections['6e82c174-d8cb-448a-ab8a-455fd77084ce']?.data ?? [];
const roomCollection = collections['5f37592e-5054-4720-8e8e-9b076df2b07b']?.['data'] ?? [];
const scheduleCollection = collections['ee9baa8f-ba17-4f4b-b915-bb972423be13']?.data ?? [];

// Payload from event
const payload = event.value || event;

// Destructure, supporting both staff_ids and teacher_ids
const {
  teacher,
  teacherId,
  staff_ids = [],
  teacher_ids = [],
  teacher_names = [],
  class: className,
  classId,
  day,
  dayId,
  period,
  periodId,
  subjectId,
  roomId,
  id,
  meeting_name,
  notes
} = payload;

// --- Find reference objects ---
const dayObj = daysCollection.find(d => d.name_de === day);
const periodObj = periodsCollection.find(p => Number(p.block_number) === Number(period));
const classObj = classCollection.find(cls => cls.id === classId);
const subjectObj = subjectCollection.find(subj => subj.id === subjectId);
const roomObj = roomCollection.find(r => r.id === roomId);

// --- Compose teacher ID array, preferring staff_ids, fallback to teacher_ids, fallback to teacherId ---
const allTeacherIds =
  Array.isArray(staff_ids) && staff_ids.length > 0 ? staff_ids.map(String) :
  Array.isArray(teacher_ids) && teacher_ids.length > 0 ? teacher_ids.map(String) :
  teacherId ? [String(teacherId)] : [];

// --- Compose teacher names ---
const composedTeacherNames =
  Array.isArray(teacher_names) && teacher_names.length > 0
    ? teacher_names
    : allTeacherIds.length > 0
      ? allTeacherIds
          .map(tid => {
            const staff = staffCollection.find(s => s.id === tid);
            return staff ? `${staff.first_name || ''} ${staff.last_name || ''}`.trim() : '';
          })
          .filter(Boolean)
      : teacher
        ? [teacher]
        : [];

// --- Check for a matching scheduled lesson (supports multiple teachers) ---
const matchingScheduleExists = scheduleCollection.some(schedule => {
  const scheduleTeacherIds = Array.isArray(schedule.teacher_ids) ? schedule.teacher_ids.map(String) : [];
  const teacherMatch = allTeacherIds.length > 0
    ? allTeacherIds.some(tid => scheduleTeacherIds.includes(tid))
    : false;
  const match = (
    String(schedule.class_id) === String(classId) &&
    String(schedule.subject_id) === String(subjectId) &&
    String(schedule.day_id) === String(dayObj?.id) &&
    String(schedule.period_id) === String(periodObj?.id) &&
    teacherMatch
  );
  return match;
});

// Compose enriched draft entry
const isTermin = subjectObj?.name === 'Termin' || subjectId === 'termin';

const entry = {
  id: id ?? `draft-${Date.now()}`,
  day_id: dayObj?.id ?? dayId ?? null,
  day_name_en: dayObj?.name_en ?? '',
  day_name_de: dayObj?.name_de ?? day ?? '',
  period_id: periodObj?.id ?? periodId ?? null,
  block_number: periodObj?.block_number ?? period,
  course_id: null,
  course_name: '',
  class_id: classObj?.id ?? classId ?? null,
  class_name: classObj?.name ?? className ?? '',
  subject_id: subjectObj?.id ?? subjectId ?? null,
  subject_name: isTermin ? 'Termin' : (subjectObj?.name ?? ''),
  room_id: roomObj?.id ?? roomId ?? null,
  room_name: roomObj?.name ?? '',
  display_cell: classObj && subjectObj
    ? `${classObj.name} ${subjectObj.name?.slice(0, 2) ?? ''}`
    : subjectObj
      ? subjectObj.name?.slice(0, 2) ?? ''
      : classObj
        ? classObj.name
        : '',
  class_grade_level: classObj?.grade_level ?? null,
  subject_color: subjectObj?.color ?? null,
  subject_icon_id: subjectObj?.icon_id ?? null,
  isDraft: true,
  currentlyScheduled: matchingScheduleExists,
  staff_ids: allTeacherIds,
  teacher_names: composedTeacherNames,
  ...(isTermin ? { meeting_name, notes } : {})
};

return entry;

