const entries = variables['af517f07-181d-4118-a343-8193085cf571'] || [];

// Payload from event
const payload = event.value || event;

// Multi-teacher support
const staff_ids = (payload.staff_ids && Array.isArray(payload.staff_ids) && payload.staff_ids.length)
  ? payload.staff_ids.map(String)
  : (payload.teacherId ? [String(payload.teacherId)] : []);
const teacher_names = Array.isArray(payload.teacher_names) && payload.teacher_names.length
  ? payload.teacher_names
  : staff_ids.map(id => {
    const t = staffCollection.find(s => (s.user_id || s.id) === id);
    return t ? `${t.first_name || ''} ${t.last_name || ''}`.trim() : '';
  }).filter(Boolean);

const { day, period, subjectId, classId, roomId } = payload;

console.log('ðŸ“¦ Payload input:', payload);

// --- Find reference objects ---
const dayObj = daysCollection.find(d => d.name_de === day);
const periodObj = periodsCollection.find(p => Number(p.block_number) === Number(period));
const classObj = classCollection.find(cls => cls.id === classId);
const subjectObj = subjectCollection.find(subj => subj.id === subjectId);
const roomObj = roomCollection.find(r => r.id === roomId);

// 1ï¸âƒ£ Check if a matching lesson already exists at this time slot FOR THE SAME CLASS
const existingEntry = entries.find(entry =>
  String(entry.day_id) === String(dayObj?.id) &&
  String(entry.period_id) === String(periodObj?.id) &&
  String(entry.class_id) === String(classObj?.id ?? classId)
);

if (existingEntry) {
  existingEntry.class_id = classObj?.id ?? classId;
  existingEntry.class_name = classObj?.name ?? '';
  existingEntry.class_grade_level = classObj?.grade_level ?? null;

  existingEntry.subject_id = subjectObj?.id ?? subjectId;
  existingEntry.subject_name = subjectObj?.name ?? '';
  existingEntry.subject_color = subjectObj?.color ?? null;
  existingEntry.subject_icon_id = subjectObj?.icon_id ?? null;

  existingEntry.room_id = roomObj?.id ?? roomId;
  existingEntry.room_name = roomObj?.name ?? '';

  existingEntry.display_cell = classObj && subjectObj
    ? `${classObj.name} ${subjectObj.name?.slice(0, 2) ?? ''}`
    : subjectObj
      ? subjectObj.name?.slice(0, 2) ?? ''
      : classObj?.name ?? '';

  existingEntry.staff_ids = staff_ids;
  existingEntry.teacher_names = teacher_names;

  // ðŸ” Recalculate whether it's still scheduled after the change
  const stillScheduled = scheduleCollection.some(schedule => {
    const teacherMatch = Array.isArray(schedule.teacher_ids)
      ? schedule.teacher_ids.some(id => staff_ids.includes(id))
      : false;
    return (
      String(schedule.class_id) === String(existingEntry.class_id) &&
      String(schedule.subject_id) === String(existingEntry.subject_id) &&
      String(schedule.day_id) === String(existingEntry.day_id) &&
      String(schedule.period_id) === String(existingEntry.period_id) &&
      teacherMatch
    );
  });
  existingEntry.currentlyScheduled = stillScheduled;

  console.log(`ðŸ” Updated existing lesson at ${day} Block ${period} for class ${existingEntry.class_name}`);
  return true;
}

// 2ï¸âƒ£ Check for a matching scheduled lesson (room check removed)
const matchingScheduleExists = scheduleCollection.some(schedule => {
  const teacherMatch = Array.isArray(schedule.teacher_ids)
    ? schedule.teacher_ids.some(id => staff_ids.includes(id))
    : false;
  const match = (
    String(schedule.class_id) === String(classId) &&
    String(schedule.subject_id) === String(subjectId) &&
    String(schedule.day_id) === String(dayObj?.id) &&
    String(schedule.period_id) === String(periodObj?.id) &&
    teacherMatch
  );

  console.log('ðŸ” Checking against schedule:', {
    match,
    payload_class_id: classId,
    payload_subject_id: subjectId,
    payload_day_id: dayObj?.id,
    payload_period_id: periodObj?.id,
    schedule_class_id: schedule.class_id,
    schedule_subject_id: schedule.subject_id,
    schedule_day_id: schedule.day_id,
    schedule_period_id: schedule.period_id,
    schedule_teacher_ids: schedule.teacher_ids,
    teacherMatch
  });

  return match;
});

console.log(
  matchingScheduleExists
    ? 'âœ… Matching scheduled lesson found â€” setting currentlyScheduled: true'
    : 'âŒ No matching scheduled lesson found â€” setting currentlyScheduled: false'
);

// 3ï¸âƒ£ Compose enriched draft entry if none existed before
const entry = {
  id: payload.id ?? `draft-${Date.now()}`,
  day_id: dayObj?.id ?? null,
  day_name_en: dayObj?.name_en ?? '',
  day_name_de: dayObj?.name_de ?? '',
  period_id: periodObj?.id ?? null,
  block_number: periodObj?.block_number ?? period,
  course_id: null,
  course_name: '',
  class_id: classObj?.id ?? classId,
  subject_id: subjectObj?.id ?? subjectId,
  room_id: roomObj?.id ?? roomId,
  room_name: roomObj?.name ?? '',
  display_cell: classObj && subjectObj
    ? `${classObj.name} ${subjectObj.name?.slice(0, 2) ?? ''}`
    : subjectObj
      ? subjectObj.name?.slice(0, 2) ?? ''
      : classObj
        ? classObj.name
        : '',
  class_name: classObj?.name ?? '',
  subject_name: subjectObj?.name ?? '',
  class_grade_level: classObj?.grade_level ?? null,
  subject_color: subjectObj?.color ?? null,
  subject_icon_id: subjectObj?.icon_id ?? null,
  isDraft: true,
  currentlyScheduled: matchingScheduleExists,
  staff_ids,
  teacher_names,
};

return entry;

