const assignments = variables['af517f07-181d-4118-a343-8193085cf571'] ?? [];

const staffSeen = new Map();
const roomSeen = new Map();
const conflictKeys = new Set();
const conflicts = [];

function getKey({ course_id, day_id, period_id }) {
  return `${course_id}-${day_id}-${period_id}`;
}

function addConflict(existing, current) {
  const existingKey = getKey(existing);
  const currentKey = getKey(current);

  if (!conflictKeys.has(existingKey)) {
    conflicts.push(existing);
    conflictKeys.add(existingKey);
  }
  if (!conflictKeys.has(currentKey)) {
    conflicts.push(current);
    conflictKeys.add(currentKey);
  }
}

for (const assignment of assignments) {
  const { day_id, period_id, course_id, staff_ids, room_id } = assignment;

  // Staff conflict check
  if (Array.isArray(staff_ids)) {
    for (const staff of staff_ids) {
      const key = `${day_id}-${period_id}-staff-${staff.id}`;
      if (staffSeen.has(key)) {
        addConflict(staffSeen.get(key), assignment);
      } else {
        staffSeen.set(key, assignment);
      }
    }
  }

  // Room conflict check
  if (room_id?.id) {
    const key = `${day_id}-${period_id}-room-${room_id.id}`;
    if (roomSeen.has(key)) {
      addConflict(roomSeen.get(key), assignment);
    } else {
      roomSeen.set(key, assignment);
    }
  }
}

return conflicts;

