return (collections['c7aa9144-7d7f-4269-a7ba-af0ea93ff8a1']?.['data'] || []).sort((a, b) => {
  // Check if each staff member is in possible staff array
  const aIsPossible = possibleStaff.includes(`${a.first_name} ${a.last_name}`);
  const bIsPossible = possibleStaff.includes(`${b.first_name} ${b.last_name}`);

  // If one is possible and other isn't, sort possible first
  if (aIsPossible && !bIsPossible) return -1;
  if (!aIsPossible && bIsPossible) return 1;

  // If both are possible or both aren't, sort by last name
  return a.last_name.localeCompare(b.last_name);
});

