const recurrence = context.component?.variables?.['5a384279-14a4-49d8-a236-ba57d8b89b3c'];
const startDate = recurrence?.startDate ? new Date(recurrence.startDate) : new Date();

const day = startDate.getDate();
const weekdayIndex = startDate.getDay();
const weekdayNames = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
const ordinals = ["first", "second", "third", "fourth", "fifth"];

function getOrdinalSuffix(n) {
  const j = n % 10, k = n % 100;
  if (j === 1 && k !== 11) return n + "st";
  if (j === 2 && k !== 12) return n + "nd";
  if (j === 3 && k !== 13) return n + "rd";
  return n + "th";
}

const weekNumber = Math.floor((day - 1) / 7) + 1;

return [
  {
    label: `On the ${getOrdinalSuffix(day)} of every month`,
    value: "day"
  },
  {
    label: `On the ${ordinals[weekNumber - 1]} ${weekdayNames[weekdayIndex]} of every month`,
    value: "weekday"
  }
];

