if (!recurrence.endDate || isNaN(new Date(recurrence.endDate).getTime())) {
  return '<span style="color: red;">âš ï¸ Bitte Enddatum wÃ¤hlen</span>';
}

const dayNamesFull = ['Sonntag', 'Montag', 'Dienstag', 'Mittwoch', 'Donnerstag', 'Freitag', 'Samstag'];

function formatDate(dateStr) {
  const d = new Date(dateStr);
  return d.toLocaleDateString('de-DE', { day: '2-digit', month: '2-digit', year: 'numeric' });
}

const num = recurrence.repeatEveryNumber || 1;

const unitMap = {
  day: 'Tag',
  week: 'Woche',
  month: 'Monat',
  year: 'Jahr'
};
const unit = unitMap[recurrence.repeatEveryUnit] || 'Tag';

const hasWeekDays = Array.isArray(recurrence.weekDays) && recurrence.weekDays.length > 0;

const startFormatted = formatDate(recurrence.startDate);
const endFormatted = formatDate(recurrence.endDate);

let repeatPhrase = `Wiederholt sich ab dem ${startFormatted} jede${num === 1 ? '' : ' ' + num + 'te'} ${unit}`;

if (hasWeekDays) {
  // Sort weekdays ascending
  const sortedDays = recurrence.weekDays.slice().sort((a, b) => a - b);

  // Group consecutive weekdays
  let groups = [];
  let group = [sortedDays[0]];

  for (let i = 1; i < sortedDays.length; i++) {
    if (sortedDays[i] === sortedDays[i - 1] + 1) {
      group.push(sortedDays[i]);
    } else {
      groups.push(group);
      group = [sortedDays[i]];
    }
  }
  groups.push(group);

  // Format groups with either "Montag" or "von Montag bis Mittwoch"
  const formattedGroups = groups.map(g => {
    if (g.length === 1) {
      return dayNamesFull[g[0]];
    } else {
      return `von ${dayNamesFull[g[0]]} bis ${dayNamesFull[g[g.length - 1]]}`;
    }
  });

  // Build phrase preserving order and handling "am" for single days nicely
  let daysPhraseParts = formattedGroups.map(grp => {
    if (grp.startsWith('von ')) {
      return grp;
    } else {
      return 'am ' + grp;
    }
  });

  let daysPhrase = '';
  let bufferSingles = [];

  function flushSingles() {
    if (bufferSingles.length === 1) {
      daysPhrase += (daysPhrase ? ' und ' : '') + 'am ' + bufferSingles[0];
    } else if (bufferSingles.length > 1) {
      daysPhrase += (daysPhrase ? ' und ' : '') + 'am ' + bufferSingles.join(' und ');
    }
    bufferSingles = [];
  }

  for (const part of daysPhraseParts) {
    if (part.startsWith('am ')) {
      bufferSingles.push(part.slice(3));
    } else {
      flushSingles();
      daysPhrase += (daysPhrase ? ' und ' : '') + part;
    }
  }
  flushSingles();

  repeatPhrase += ' ' + daysPhrase + ` bis zum ${endFormatted}`;
} else {
  repeatPhrase += ` bis zum ${endFormatted}`;
}

return repeatPhrase;

