// Helper
const toArray = v => Array.isArray(v) ? v : (v && typeof v === 'object' ? Object.values(v) : []);

// 1) Current profile
const currentProfileId = collections['a3bd1e48-4a7d-4146-94db-4cb1320be5a7']?.data?.[0]?.id;
if (!currentProfileId) return [];

// 2) Family members
const familyMembers = toArray(collections['b2401a50-b8b7-4e37-a9fd-94c2e3722469']?.data);

// 3) Current member + family_id
const currentMember = familyMembers.find(m => m?.profile_id === currentProfileId);
const currentFamilyId = currentMember?.family_id;
if (!currentFamilyId) return [];

// 4) Other student members in the same family
const studentFamilyMembers = familyMembers.filter(m =>
  m?.family_id === currentFamilyId &&
  m?.profile_id !== currentProfileId &&
  String(m?.role || '').toLowerCase() === 'student'
);

// 5) Students (FIXED collection ID)
const students = toArray(collections['881b151d-4f64-4d96-a74b-812bb9282cb8']?.data);

// 6) Match students to family members by profile_id = student_id
const matchingStudents = students.filter(s =>
  studentFamilyMembers.some(m => m?.profile_id === s?.student_id)
);

// 7) Classes (for grade_level, etc.)
const classes = toArray(collections['b8d0c0e2-1e1a-49c1-b2e7-6d468ab46eb2']?.data);
const classById = new Map(classes.map(c => [c.id, c]));
const classByName = new Map(classes.map(c => [String(c?.name || '').trim().toUpperCase(), c]));

// 8) Enrich with class + grade
const enriched = matchingStudents.map(s => {
  const classId = s.class_id ?? s.classId ?? s.structure_class_id ?? null;
  const className = s.class_name ?? s.className ?? null;

  let cls = classId ? classById.get(classId) : undefined;
  if (!cls && className) cls = classByName.get(String(className).trim().toUpperCase());

  let grade = cls?.grade_level;
  if (!Number.isFinite(grade) && className) {
    const m = String(className).match(/^(\d+)/);
    if (m) grade = parseInt(m[1], 10);
  }
  if (!Number.isFinite(grade) && Number.isFinite(s?.grade_level)) grade = s.grade_level;
  if (!Number.isFinite(grade) && s?.grade_level != null && !Number.isNaN(Number(s.grade_level))) {
    grade = Number(s.grade_level);
  }

  return {
    ...s,
    class_id: cls?.id ?? classId ?? null,
    class_name: cls?.name ?? className ?? null,
    grade_level: Number.isFinite(grade) ? grade : null,
    class_year: cls?.year ?? null,
    class_color: cls?.color ?? null,
  };
});

return enriched;

