return (() => {
  const COL_ID = 'a2df9501-1d71-4d48-bfc2-9609d7e76dd6';
  const STUDENT_GRADE =
    Number(variables['dbb33dce-f242-4fe9-8aa9-1712edef6c62']?.student?.grade_level);

  // Helpers
  const toArray = (x) =>
    Array.isArray(x) ? x : x && typeof x === 'object' ? Object.values(x) : [];
  const stripHtml = (html) =>
    String(html || '').replace(/<[^>]*>/g, '').replace(/\s+/g, ' ').trim();

  // Load rows
  const src = collections?.[COL_ID];
  const rows =
    toArray(src?.data).length ? toArray(src?.data)
    : toArray(src?.rows).length ? toArray(src?.rows)
    : toArray(src);

  if (!rows.length) return [];

  // Day ID -> label (matches your source where 2=Montag ... 1=Sonntag)
  const dayNames = {
    2: 'Montag',
    3: 'Dienstag',
    4: 'Mittwoch',
    5: 'Donnerstag',
    6: 'Freitag',
    7: 'Samstag',
    1: 'Sonntag'
  };

  // Prefer schedule_slots; fallback to schedule_day_ids + schedule_time_ranges
  const normalizeSlots = (r) => {
    const slotsRaw = r?.schedule_slots;
    if (slotsRaw && (Array.isArray(slotsRaw) || typeof slotsRaw === 'object')) {
      const arr = Array.isArray(slotsRaw) ? slotsRaw : Object.values(slotsRaw);
      return arr
        .map((s) => ({
          day_id: Number(s?.day_id ?? s?.dayId ?? s?.day),
          start: s?.start_time ?? s?.start ?? s?.startTime,
          end: s?.end_time ?? s?.end ?? s?.endTime
        }))
        .filter((s) => !!s.day_id && s.start && s.end);
    }

    const dayIds = toArray(r?.schedule_day_ids).map(Number).filter(Boolean);
    const ranges = toArray(r?.schedule_time_ranges).map(String);
    const n = Math.max(dayIds.length, ranges.length);
    const out = [];
    for (let i = 0; i < n; i++) {
      const d = Number(dayIds[i]);
      const tr = (ranges[i] || '').trim();
      if (!d || !tr.includes('-')) continue;
      const [start, end] = tr.split('-').map((x) => x.trim());
      if (start && end) out.push({ day_id: d, start, end });
    }
    return out;
  };

  const modules = [];

  for (const r of rows) {
    // Grade-level filter: keep only if student's grade is present in row.grade_levels
    if (Number.isFinite(STUDENT_GRADE)) {
      const gradeList = toArray(r?.grade_levels)
        .map((g) => Number(g))
        .filter((g) => Number.isFinite(g));
      // Exclude if no declared grades or student's grade not in list
      if (!gradeList.length || !gradeList.includes(STUDENT_GRADE)) continue;
    }

    const slots = normalizeSlots(r);
    if (!slots.length) continue;

    // Prepare info fields
    const pictures = toArray(r?.course_pictures)
      .map(String)
      .map((u) => u.trim())
      .filter(Boolean);
    const picture_first = r?.course_picture_first || pictures[0] || null;

    // Use the parents-allowed description if provided; fall back to general
    const descForParents = r?.course_description_for_parents ?? null;
    const descGeneral = r?.course_description ?? null;

    // Strip HTML to display clean text in the component
    const description_for_parents = stripHtml(descForParents);
    const description = stripHtml(descGeneral);

    // Collapse multiple slots on same day → min start / max end (string compare ok for HH:MM)
    const byDay = {};
    for (const s of slots) {
      const d = s.day_id;
      if (!byDay[d]) byDay[d] = { start: s.start, end: s.end };
      else {
        if (s.start < byDay[d].start) byDay[d].start = s.start;
        if (s.end > byDay[d].end) byDay[d].end = s.end;
      }
    }

    for (const [dStr, range] of Object.entries(byDay)) {
      modules.push({
        window_id: String(r.window_id),
        day: dayNames[Number(dStr)] || `Tag ${dStr}`,
        title: r.course_name || r.course_code || 'Kurs',
        meta: `${range.start}-${range.end}`,
        // Extra info for "Mehr Info"
        course_id: r.course_id || null,
        course_code: r.course_code || null,
        description_visible_to_parents: !!r.description_visible_to_parents,
        description, // plain text (HTML stripped)
        description_for_parents, // plain text (HTML stripped)
        wichtige_infos: r?.course_wichtige_infos || null,
        pictures,
        picture_first
      });
    }
  }

  // Sort by weekday, then start time, then title
  const dayOrder = ['Montag', 'Dienstag', 'Mittwoch', 'Donnerstag', 'Freitag', 'Samstag', 'Sonntag'];
  const toMin = (hhmm) => {
    const [h, m] = String(hhmm || '').split(':'); const H = +h, M = +m;
    return Number.isFinite(H) && Number.isFinite(M) ? H * 60 + M : 9999;
  };
  modules.sort((a, b) => {
    const ai = dayOrder.indexOf(a.day), bi = dayOrder.indexOf(b.day);
    if (ai !== bi) return (ai < 0 ? 99 : ai) - (bi < 0 ? 99 : bi);
    const t = toMin((a.meta || '').split('-')[0]) - toMin((b.meta || '').split('-')[0]);
    return t !== 0 ? t : String(a.title || '').localeCompare(String(b.title || ''), 'de-DE');
  });

  return modules;
})()

