const student = variables['91a37c0c-4675-4e88-938b-55f2b193e266'];
const schedules = collections['ee9baa8f-ba17-4f4b-b915-bb972423be13']?.data || [];

const blockNumber = context.item.data?.['block_number'];
const dayId = context.item.parent?.data?.['day_id'];

if (!student?.class_id || blockNumber === undefined || dayId === undefined) {
    return null;
}

const match = schedules.find(schedule =>
    schedule.class_id === student.class_id &&
    schedule.block_number === blockNumber &&
    schedule.day_id === dayId
);

if (!match) return null;

const label = match.subject_name || match.course_name || null;

if (!label) return null;

// Show short version if screen width ≤ 768px
return window.innerWidth <= 768 ? label.slice(0, 3) : label;

