// Build ONLY the contacts array for the GoHomeSettings component.
// Adults only (parents/guardians). No children/siblings in the result.

// 1) Get the current student id from the interview step state
const state = variables['dbb33dce-f242-4fe9-8aa9-1712edef6c62'] || {};
const student = state.student || {};
const studentId = student.student_id ?? student.profile_id ?? null;
if (!studentId) return [];

// 2) Load the "students + family" collection (supports common WeWeb shapes)
let raw =
  collections['d62b8548-7f35-4a50-a1b3-fd77bb8879a2']?.data ??
  context.workflow['d62b8548-7f35-4a50-a1b3-fd77bb8879a2']?.result?.data;

try { raw = JSON.parse(JSON.stringify(raw)); } catch {}

const all = Array.isArray(raw) ? raw
  : Array.isArray(raw?.items) ? raw.items
  : Array.isArray(raw?.rows) ? raw.rows
  : Array.isArray(raw?.results) ? raw.results
  : (raw && typeof raw === 'object' ? Object.values(raw) : []);

// 3) Find the current student row
const studentRow = all.find(r => r?.profile_id === studentId || r?.student_id === studentId);
if (!studentRow) return [];

// 4) Helpers
const toArray = v => Array.isArray(v) ? v : (v ? [v] : []);
const dedupePushById = (arr, obj) => {
  if (!obj?.id) return;
  if (!arr.some(x => x.id === obj.id)) arr.push(obj);
};
const extractPhone = (obj) =>
  obj?.phone ?? obj?.mobile ?? obj?.phone_number ?? obj?.telephone ?? '';

const normalizeRelationDE = (person) => {
  const relDesc = (person?.relation_description || '').toString().trim();
  const role = (person?.role || '').toString().trim().toLowerCase();
  const firstLower = (person?.first_name || '').toString().trim().toLowerCase();

  if (['father', 'vater', 'dad', 'papa'].includes(firstLower)) return 'Vater';
  if (['mother', 'mutter', 'mom', 'mama'].includes(firstLower)) return 'Mutter';

  const relMap = {
    'Parent': 'Elternteil',
    'Guardian': 'Bezugsperson',
    'guardian': 'Bezugsperson',
    'parent': 'Elternteil',
    'mother': 'Mutter',
    'father': 'Vater'
  };
  if (relMap[relDesc]) return relMap[relDesc];
  if (relMap[role]) return relMap[role];
  return 'Bezugsperson';
};

// 5) Build contacts: ADULTS ONLY
const contacts = [];
for (const p of toArray(studentRow.parents)) {
  const id = p?.profile_id;
  if (!id) continue;

  const contact = {
    id,
    first_name: p?.first_name ?? '',
    last_name: p?.last_name ?? '',
    relation: normalizeRelationDE(p),
    phone: extractPhone(p)
  };
  dedupePushById(contacts, contact);
}

// Return only the contacts array (bind this to content.contacts)
return contacts;

