const currentProfileId = collections['a3bd1e48-4a7d-4146-94db-4cb1320be5a7']?.['data']?.[0]?.['id'];

// Get all family members from the collection
let familyMembers = collections['b2401a50-b8b7-4e37-a9fd-94c2e3722469']?.['data'];

// Defensive: If familyMembers is not an array, convert it to one or default to empty
if (!Array.isArray(familyMembers)) {
  if (familyMembers && typeof familyMembers === 'object') {
    familyMembers = Object.values(familyMembers);
  } else {
    familyMembers = [];
  }
}

// Find the current member object to get their family_id
const currentMember = familyMembers.find(member => member.profile_id === currentProfileId);
const currentFamilyId = currentMember?.family_id;

// Filter: same family_id, not the current profile, and role is "student"
const studentFamilyMembers = familyMembers.filter(member =>
  member.family_id === currentFamilyId &&
  member.profile_id !== currentProfileId &&
  member.role === "student"
);

// Get the student details from the students collection
let students = collections['881b151d-4f64-4d96-a74b-812bb9282cb8']?.['data'];
if (!Array.isArray(students)) {
  if (students && typeof students === 'object') {
    students = Object.values(students);
  } else {
    students = [];
  }
}
const matchingStudents = students.filter(student =>
  studentFamilyMembers.some(member => member.profile_id === student.student_id)
);

return matchingStudents;

