const lang = 'de'; // or read from a site/app setting if you have one
const actions = {
  canEditWishes: true,
  canEditHeimweg: true,
  // editWishesLink: '/edit-wishes',
  // editHeimwegLink: '/edit-pickup'
};

// ID of the Student Profile collection you showed
const STUDENT_PROFILE_COLL_ID = 'd62b8548-7f35-4a50-a1b3-fd77bb8879a2';

function transformStudentSchedule(options) {
  const {
    variables: vars = typeof variables !== 'undefined' ? variables : {},
    collections: colls = typeof collections !== 'undefined' ? collections : {},
    lang = 'de',
    actions = { canEditWishes: false, canEditHeimweg: false }
  } = options || {};

  // 1) Interview data
  const interview = vars['dbb33dce-f242-4fe9-8aa9-1712edef6c62'] || {};
  const student = interview.student || {};
  const courseChoices = Array.isArray(interview.course_choices) ? interview.course_choices : [];
  const heimweg = interview.heimweg || {};

  // 2) Collections
  const daysRaw = colls['18b475b5-a69a-4000-8dfe-1cb5558734f5']?.data || [];
  const windows = colls['a2df9501-1d71-4d48-bfc2-9609d7e76dd6']?.data || [];

  // Helpers
  const norm = (s) => (s || '').toString().trim().toLowerCase();
  const trim = (s) => (s || '').toString().trim();

  // Filter days to student's school if present
  const daysFiltered = student.school_id
    ? daysRaw.filter(d => !d.school_id || d.school_id === student.school_id)
    : daysRaw.slice();

  // Fallback if empty
  let daysSource = daysFiltered.length ? daysFiltered : [
    { day_id: 2, day_number: 1, name_de: 'Montag',     name_en: 'Monday' },
    { day_id: 3, day_number: 2, name_de: 'Dienstag',   name_en: 'Tuesday' },
    { day_id: 4, day_number: 3, name_de: 'Mittwoch',   name_en: 'Wednesday' },
    { day_id: 5, day_number: 4, name_de: 'Donnerstag', name_en: 'Thursday' },
    { day_id: 6, day_number: 5, name_de: 'Freitag',    name_en: 'Friday' }
  ];

  // Sort and build mapping: any of {name_de, name_en} -> canonical German key (name_de)
  daysSource = daysSource.slice().sort((a, b) => (a.day_number || 0) - (b.day_number || 0));
  const nameToDeKey = {};
  for (const d of daysSource) {
    const de = d.name_de || '';
    const en = d.name_en || '';
    if (de) nameToDeKey[norm(de)] = de;
    if (en) nameToDeKey[norm(en)] = de;
  }

  // Days for UI
  const days = daysSource.map(d => ({
    id: d.day_id ?? d.day_number ?? d.name_de ?? d.name_en ?? Math.random().toString(36).slice(2),
    key: d.name_de || '',
    label: lang === 'de' ? (d.name_de || '') : (d.name_en || d.name_de || '')
  }));

  // Window lookup
  const windowLookup = {};
  for (const w of windows) {
    if (w?.window_id) windowLookup[w.window_id] = { course_name: w.course_name || '—' };
  }

  // Group choices by day
  const choicesByDay = {};
  for (const d of days) choicesByDay[d.key] = [];
  for (const choice of courseChoices) {
    const raw = choice?.day || '';
    const deKey = nameToDeKey[norm(raw)];
    if (deKey && choicesByDay[deKey]) choicesByDay[deKey].push(choice);
  }
  for (const k of Object.keys(choicesByDay)) {
    choicesByDay[k].sort((a, b) => (a.rank ?? 0) - (b.rank ?? 0));
  }

  // Extract fallback info per day (ignore fallback if day is explicitly no_offer at rank 1)
  const fallbackByDay = {};
  const fallbackRankByDay = {};
  for (const dayKey of Object.keys(choicesByDay)) {
    const list = choicesByDay[dayKey] || [];
    if (list.length && list[0].no_offer === true) continue;

    const fallbackItem = list.find(ch => ch && ch.pickup_fallback === true);
    if (fallbackItem) {
      fallbackByDay[dayKey] = true;
      const maxRank = Math.max(0, ...list.map(ch => ch?.rank ?? 0));
      fallbackRankByDay[dayKey] = (Number.isFinite(fallbackItem.rank) && fallbackItem.rank > 0)
        ? fallbackItem.rank
        : (maxRank || 3);
    }
  }

  // Max rows must cover fallback rank too
  let maxWishesRows = 1;
  for (const dayKey of Object.keys(choicesByDay)) {
    const list = choicesByDay[dayKey] || [];
    const first = list[0];

    // Count real selections only (exclude fallback)
    const real = (first && first.no_offer === true)
      ? 1 // show "Kein Modul" in row 1 when no_offer
      : list.filter(ch => ch?.window_id && ch.no_offer !== true && !ch.pickup_fallback).length;

    const fallbackRank = fallbackRankByDay[dayKey] || 0;
    maxWishesRows = Math.max(maxWishesRows, real, fallbackRank, 1);
  }

  // Wishes by day (respect no_offer as rank 1; exclude fallback entries)
  const wishesByDay = {};
  for (const d of days) {
    const list = choicesByDay[d.key] || [];
    if (list.length && list[0].no_offer === true) {
      wishesByDay[d.key] = [{ rank: 1, title: lang === 'de' ? 'Kein Modul' : 'No module' }];
    } else {
      const realChoices = list.filter(ch => ch?.window_id && !ch.pickup_fallback);
      wishesByDay[d.key] = realChoices.map(ch => {
        const title = windowLookup[ch.window_id]?.course_name || '—';
        return { rank: ch.rank ?? 0, title };
      });
    }
  }

  // Has module
  const hasModule = {};
  for (const d of days) {
    const list = choicesByDay[d.key] || [];
    hasModule[d.key] = list.some(ch => ch.window_id != null && ch.no_offer !== true);
  }

  // Pickup per day (generic, no names)
  const pickupByDay = {};
  const methodText = (methodId) => {
    const de = {
      goes_alone: 'geht allein nach Hause',
      authorized_pickup: 'wird abgeholt',
      public_transport: 'fährt mit öffentlichen Verkehrsmitteln nach Hause',
      school_bus: 'fährt mit dem Schulbus / Shuttle nach Hause',
      none: '—'
    };
    const en = {
      goes_alone: 'goes home alone',
      authorized_pickup: 'is picked up',
      public_transport: 'takes public transport home',
      school_bus: 'takes the school bus/shuttle home',
      none: '—'
    };
    const dict = lang === 'de' ? de : en;
    return dict[methodId] || dict.none;
  };
  for (const d of days) {
    const time = hasModule[d.key] ? '16:00' : '14:30';
    let method = heimweg.method || 'none';
    if (heimweg.method === 'varies_daily' && heimweg.byDay?.[d.key]) {
      method = heimweg.byDay[d.key].method || 'none';
    }
    const text = methodText(method);
    const note = (heimweg.method === 'varies_daily' && heimweg.byDay?.[d.key]?.notes) ? heimweg.byDay[d.key].notes : '';
    pickupByDay[d.key] = { time, method, text, warning: '', note };
  }

  // Authorized contacts resolution

  // Start with interview contacts
  const contactMap = {};
  const contactsArr = Array.isArray(heimweg.contacts) ? heimweg.contacts : [];
  for (const c of contactsArr) {
    if (!c?.id) continue;
    const name = trim(`${trim(c.first_name)} ${trim(c.last_name)}`);
    if (name) contactMap[c.id] = name;
  }

  const authorizedIds = Array.isArray(heimweg.authorizedIds) ? heimweg.authorizedIds : [];
  const primaryId = heimweg.primaryContactId;
  const authorizedIdSet = new Set(authorizedIds);

  // 1) Augment from Student Profile => parents[]
  const profiles = colls[STUDENT_PROFILE_COLL_ID]?.data || [];
  const studentProfile = profiles.find(p => p?.profile_id === student?.student_id);
  if (studentProfile && Array.isArray(studentProfile.parents)) {
    for (const p of studentProfile.parents) {
      const pid = p?.profile_id;
      if (!pid || !authorizedIdSet.has(pid)) continue; // only index those actually authorized
      const name = trim(`${trim(p.first_name)} ${trim(p.last_name)}`);
      if (name && !contactMap[pid]) contactMap[pid] = name;
    }
  }

  // 2) Fallback: scan all loaded collections for matching IDs (broad safety net)
  const idFields = ['id', 'profile_id', 'contact_id', 'person_id', 'guardian_id', 'parent_id', 'user_id'];
  const nameFields = ['name', 'full_name', 'display_name'];
  const firstFields = ['first_name', 'firstname', 'given_name'];
  const lastFields = ['last_name', 'lastname', 'family_name'];

  for (const c of Object.values(colls || {})) {
    const arr = Array.isArray(c?.data) ? c.data : [];
    for (const row of arr) {
      if (!row || typeof row !== 'object') continue;
      let rowId;
      for (const f of idFields) {
        if (row[f] && authorizedIdSet.has(row[f])) { rowId = row[f]; break; }
      }
      if (!rowId || contactMap[rowId]) continue;

      // Build a display name
      let display = '';
      for (const nf of nameFields) {
        if (trim(row[nf])) { display = trim(row[nf]); break; }
      }
      if (!display) {
        let first = '';
        let last = '';
        for (const ff of firstFields) if (!first && trim(row[ff])) first = trim(row[ff]);
        for (const lf of lastFields) if (!last && trim(row[lf])) last = trim(row[lf]);
        display = trim(`${first} ${last}`) || first || last || '';
      }
      if (display) contactMap[rowId] = display;
    }
  }

  // Build global authorized list in the order of authorizedIds
  const rows = [];
  for (const id of authorizedIds) {
    const name = contactMap[id];
    if (!name) continue;
    rows.push({ id, name, isPrimary: id === primaryId });
  }

  // Deduplicate while preserving order
  const seen = new Set();
  const dedupedRows = [];
  for (const r of rows) {
    if (seen.has(r.id)) continue;
    seen.add(r.id);
    dedupedRows.push(r);
  }

  const unresolvedIds = authorizedIds.filter(id => !contactMap[id]);
  const namesList = dedupedRows.map(r =>
    r.isPrimary ? `${r.name} (${lang === 'de' ? 'Primär' : 'Primary'})` : r.name
  ).join(', ');

  const authorizedContacts = {
    rows: dedupedRows,           // [{ id, name, isPrimary }]
    names: namesList,            // "Anna Müller (Primär), Ben Wagner"
    count: dedupedRows.length,
    unresolvedIds,               // IDs that could not be matched anywhere
    warning: dedupedRows.length
      ? (unresolvedIds.length ? `${unresolvedIds.length} ${lang === 'de' ? 'Kontakt(e) nicht gefunden' : 'contact(s) not found'}` : '')
      : (lang === 'de' ? 'Keine abholberechtigten Kontakte gesetzt' : 'No authorized contacts set')
  };

  return {
    lang,
    studentName: `${student.first_name || ''} ${student.last_name || ''}`.trim() || '—',
    studentId: student.student_id || '',
    days,
    maxWishesRows,
    wishesByDay,
    // New: fallback info for rendering "Falls nicht möglich" at the correct rank
    fallbackByDay,
    fallbackRankByDay,
    pickupByDay,
    authorizedContacts,
    actions
  };
}

// Return the computed overview object for bindings
return transformStudentSchedule({ variables, collections, lang, actions });

